const onlyOpenCheckbox = document.getElementById("onlyOpenCheckbox");
const kpiTotal = document.getElementById("kpiTotal");
const kpiTotalTrend = document.getElementById("kpiTotalTrend");
const kpiClosedResolved = document.getElementById("kpiClosedResolved");
const kpiClosedResolvedPct = document.getElementById("kpiClosedResolvedPct");
const kpiOpenBacklog = document.getElementById("kpiOpenBacklog");
const kpiOpenBacklogPct = document.getElementById("kpiOpenBacklogPct");
const kpiInReviewInProgress = document.getElementById("kpiInReviewInProgress");
const kpiInReviewInProgressPct = document.getElementById("kpiInReviewInProgressPct");
const kpiNoFix = document.getElementById("kpiNoFix");
const kpiNoFixPct = document.getElementById("kpiNoFixPct");
const ticketsTableBody = document.getElementById("ticketsTableBody");

function initFilters() {
  monthFilter.innerHTML = "";
  months.forEach(m => {
    const opt = document.createElement("option");
    opt.value = m;
    opt.textContent = formatMonthLabel(m);
    monthFilter.appendChild(opt);
  });

  entities.forEach(e => {
    const opt = document.createElement("option");
    opt.value = e;
    opt.textContent = e;
    entityFilter.appendChild(opt);
  });

  monthFilter.value = "__all__";
  entityFilter.value = "__all__";
}

let statusChart = null;

function buildStatusChart(ctx, byStatus) {
  const labels = Object.keys(byStatus);
  const values = labels.map(l => byStatus[l].length);
  const palette = {
    "Abierto": "rgba(250,204,21,0.9)",
    "Resuelto": "rgba(34,197,94,0.9)",
    "Cerrado": "rgba(146,64,14,0.9)",
    "Backlog": "rgba(234,179,8,0.9)",
    "No se corregirá": "rgba(239,68,68,0.9)",
    "En revisión": "rgba(29,78,216,0.9)",
    "En curso": "rgba(96,165,250,0.9)"
  };
  const bg = labels.map(l => palette[l] || "rgba(156,163,175,0.95)");
  return new Chart(ctx, {
    type: "bar",
    data: { labels, datasets: [{ data: values, backgroundColor: bg, borderRadius: 6 }] },
    options: {
      responsive: true,
      plugins: { legend: { display: false } },
      scales: {
        x: { ticks: { color: "#e5e7eb", font: { size: 11 } }, grid: { display: false } },
        y: { ticks: { color: "#9ca3af", font: { size: 10 } }, grid: { color: "rgba(55,65,81,.6)" } }
      }
    }
  });
}

function renderStatusSummary(byStatus) {
  statusSummary.innerHTML = "";
  const order = ["Abierto","Backlog","En curso","En revisión","Resuelto","Cerrado","No se corregirá"];
  const dotClass = {
    "Abierto": "dot-abierto",
    "Backlog": "dot-backlog",
    "En curso": "dot-encurso",
    "En revisión": "dot-enrevision",
    "Resuelto": "dot-resuelto",
    "Cerrado": "dot-cerrado",
    "No se corregirá": "dot-nosecorrigira"
  };
  order.forEach(st => {
    const items = byStatus[st] || [];
    const div = document.createElement("div");
    div.className = "status-pill";
    const dClass = dotClass[st] || "dot-default";
    div.innerHTML = `
      <div class="status-left">
        <span class="dot ${dClass}"></span>
        <span>${st}</span>
      </div>
      <span>${items.length}</span>
    `;
    statusSummary.appendChild(div);
  });
}

function renderResponsablesTable(data) {
  responsablesTable.innerHTML = "";
  if (!data.length) {
    const div = document.createElement("div");
    div.textContent = "Sin tickets para este filtro.";
    div.style.color = "#9ca3af";
    div.style.fontSize = "12px";
    responsablesTable.appendChild(div);
    return;
  }
  const byResp = groupBy(data, "Responsable");
  const rows = Object.entries(byResp).map(([name, items]) => {
    const tiempos = items.map(i => i.Tiempos).filter(v => v !== null && !Number.isNaN(v));
    const avg = tiempos.length ? tiempos.reduce((a,b)=>a+b,0) / tiempos.length : null;
    return { name: name || "Sin responsable", count: items.length, avgDays: avg };
  }).sort((a,b) => b.count - a.count);

  rows.forEach(r => {
    const row = document.createElement("div");
    row.className = "resp-row";
    const avgText = r.avgDays == null ? "s/d" : r.avgDays.toFixed(1) + " d";
    row.innerHTML = `
      <div>${r.name}</div>
      <div><span class="resp-badge">${r.count}</span></div>
      <div>${avgText}</div>
    `;
    responsablesTable.appendChild(row);
  });
}

function renderTable(data) {
  ticketsTableBody.innerHTML = "";
  if (!data.length) {
    const row = document.createElement("div");
    row.className = "table-row";
    row.innerHTML = "<div>-</div><div>-</div><div>No hay tickets</div><div>-</div>";
    ticketsTableBody.appendChild(row);
    return;
  }
  data.forEach(item => {
    const row = document.createElement("div");
    row.className = "table-row";
    const fecha = new Date((item.FechaCre2 || "").replace(" ", "T"));
    const fechaFmt = isNaN(fecha.getTime())
      ? "-"
      : fecha.toLocaleString("es-CO", {
          day: "2-digit", month: "2-digit", year: "numeric",
          hour: "2-digit", minute: "2-digit"
        });
    const estado = item.Estado || "N/A";
    row.innerHTML = `
      <div>${fechaFmt}</div>
      <div>${item.Incidencia}</div>
      <div>
        <span class="estado-pill">
          <span class="estado-dot"></span>
          <span>${estado}</span>
        </span>
      </div>
      <div>${item.Responsable || "-"}</div>
    `;
    ticketsTableBody.appendChild(row);
  });
}

function calcKPIs(filtered, selectedMonth) {
  const total = filtered.length;
  const byStatus = groupBy(filtered, "Estado");
  const cerrados = (byStatus["Cerrado"] || []).length;
  const resueltos = (byStatus["Resuelto"] || []).length;
  const abiertos = (byStatus["Abierto"] || []).length;
  const backlog = (byStatus["Backlog"] || []).length;
  const enRevision = (byStatus["En revisión"] || []).length;
  const enCurso = (byStatus["En curso"] || []).length;
  const noFix = (byStatus["No se corregirá"] || []).length;

  const closedResolved = cerrados + resueltos;
  const openBacklog = abiertos + backlog;
  const inReviewInProgress = enRevision + enCurso;

  const pct = v => total ? Math.round((v / total) * 100) : 0;

  kpiTotal.textContent = total;
  let vsText = "vs mes anterior: n/d";
  if (selectedMonth !== "__all__") {
    const idx = monthsRaw.indexOf(selectedMonth);
    if (idx > 0) {
      const prevMonth = monthsRaw[idx - 1];
      const prevTotal = ticketsData.filter(t => t.Mes === prevMonth).length;
      if (prevTotal > 0) {
        const delta = total - prevTotal;
        const p = Math.round((delta / prevTotal) * 100);
        const sign = delta > 0 ? "+" : "";
        const symbol = delta > 0 ? "↑" : (delta < 0 ? "↓" : "≈");
        vsText = `vs mes anterior: ${sign}${p}% (${symbol})`;
      }
    }
  }
  kpiTotalTrend.textContent = vsText;

  kpiClosedResolved.textContent = closedResolved;
  kpiClosedResolvedPct.textContent = `Share: ${pct(closedResolved)}%`;

  kpiOpenBacklog.textContent = openBacklog;
  kpiOpenBacklogPct.textContent = `Share: ${pct(openBacklog)}%`;

  kpiInReviewInProgress.textContent = inReviewInProgress;
  kpiInReviewInProgressPct.textContent = `Share: ${pct(inReviewInProgress)}%`;

  kpiNoFix.textContent = noFix;
  kpiNoFixPct.textContent = `Share: ${pct(noFix)}%`;
}

function applyFilters() {
  const selectedMonth = monthFilter.value;
  const selectedEntity = entityFilter.value;
  const onlyOpen = onlyOpenCheckbox.checked;

  let filtered = ticketsData.slice();
  if (selectedMonth !== "__all__") {
    filtered = filtered.filter(t => t.Mes === selectedMonth);
  }
  if (selectedEntity !== "__all__") {
    filtered = filtered.filter(t => t.Incidencia === selectedEntity);
  }

  const byStatus = groupBy(filtered, "Estado");
  calcKPIs(filtered, selectedMonth);

  const periodLabel = formatMonthLabel(selectedMonth);
  badgeContext.textContent = selectedEntity === "__all__"
    ? periodLabel
    : `${periodLabel} · ${selectedEntity}`;
  chartLegend.textContent = `Periodo: ${periodLabel}`;

  if (statusChart) statusChart.destroy();
  const ctx = document.getElementById("statusChart").getContext("2d");
  statusChart = buildStatusChart(ctx, byStatus);

  renderStatusSummary(byStatus);
  renderResponsablesTable(filtered);

  let tableBase = ticketsData.filter(t => t.Estado === "Abierto" || t.Estado === "Backlog");
  if (onlyOpen) tableBase = tableBase.filter(t => t.Estado === "Abierto");
  tableBase.sort((a,b) => {
    const da = new Date((a.FechaCre2 || "").replace(" ", "T"));
    const db = new Date((b.FechaCre2 || "").replace(" ", "T"));
    return db - da;
  });
  renderTable(tableBase);
}

initFilters();
applyFilters();
monthFilter.addEventListener("change", applyFilters);
entityFilter.addEventListener("change", applyFilters);
onlyOpenCheckbox.addEventListener("change", applyFilters);
